package com.example.saquizapp;public class QuizModelClass {    String mainQuestion;    String firstOption;    String secondOption;    String thirdOption;    String fourthOption;    String answer;    public QuizModelClass(String mainQuestion, String firstOption, String secondOption, String thirdOption, String fourthOption, String answer) {        this.mainQuestion = mainQuestion;        this.firstOption = firstOption;        this.secondOption = secondOption;        this.thirdOption = thirdOption;        this.fourthOption = fourthOption;        this.answer = answer;    }    public String getAnswer() {        return answer;    }    public void setAnswer(String answer) {        this.answer = answer;    }    public String getMainQuestion() {        return mainQuestion;    }    public void setMainQuestion(String mainQuestion) {        this.mainQuestion = mainQuestion;    }    public String getFirstOption() {        return firstOption;    }    public void setFirstOption(String firstOption) {        this.firstOption = firstOption;    }    public String getSecondOption() {        return secondOption;    }    public void setSecondOption(String secondOption) {        this.secondOption = secondOption;    }    public String getThirdOption() {        return thirdOption;    }    public void setThirdOption(String thirdOption) {        this.thirdOption = thirdOption;    }    public String getFourthOption() {        return fourthOption;    }    public void setFourthOption(String fourthOption) {        this.fourthOption = fourthOption;    }}